import React from 'react';
import { Clock } from 'lucide-react';
import CronItem from './CronItem';

const CronList = ({ crons, onEdit, onDelete, onToggle, onExecute, onViewLogs }) => {
  // Se não tem CRONs, mostrar mensagem vazia
  if (crons.length === 0) {
    return (
      <div className="p-6">
        <div className="text-center py-12">
          <Clock size={48} className="mx-auto text-gray-400 mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            Nenhum CRON configurado
          </h3>
          <p className="text-gray-600">
            Crie seu primeiro CRON para começar
          </p>
        </div>
      </div>
    );
  }

  // Mostrar lista de CRONs
  return (
    <div className="p-6">
      <div className="grid gap-4">
        {crons.map(cron => (
          <CronItem
            key={cron.id}
            cron={cron}
            onEdit={onEdit}
            onDelete={onDelete}
            onToggle={onToggle}
            onExecute={onExecute}
            onViewLogs={onViewLogs}
          />
        ))}
      </div>
    </div>
  );
};

export default CronList;
