import React from 'react';
import { Activity } from 'lucide-react';

const CronLogs = ({ cron, onClose }) => {
  return (
    <div
      className="modal show d-block"
      tabIndex="-1"
      role="dialog"
      style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}
    >
      <div className="modal-dialog modal-lg modal-dialog-centered" role="document">
        <div className="modal-content">
          {/* Cabeçalho do modal */}
          <LogsHeader cronName={cron.name} onClose={onClose} />

          {/* Conteúdo dos logs */}
          <LogsContent logs={cron.logs || []} />
        </div>
      </div>
    </div>
  );
};

// Cabeçalho do modal de logs
const LogsHeader = ({ cronName, onClose }) => (
  <div className="modal-header">
    <h5 className="modal-title">Logs - {cronName}</h5>
    <button type="button" className="btn-close" aria-label="Close" onClick={onClose}></button>
  </div>
);

// Conteúdo dos logs
const LogsContent = ({ logs }) => {
  if (logs.length === 0) {
    return (
      <div className="modal-body text-center py-5">
        <Activity size={48} className="mb-3 text-secondary" />
        <p className="text-muted">Nenhum log encontrado</p>
      </div>
    );
  }

  return (
    <div className="modal-body" style={{ maxHeight: '60vh', overflowY: 'auto' }}>
      {logs.map((log, index) => (
        <LogItem key={log.id || index} log={log} />
      ))}
    </div>
  );
};

// Item individual de log
const LogItem = ({ log }) => {
  const isSuccess = log.status === 'success';

  return (
    <div className={`mb-3 p-3 border-start ${isSuccess ? 'border-success bg-light' : 'border-danger bg-light'}`}>
      {/* Cabeçalho do log */}
      <div className="d-flex justify-content-between mb-2">
        <span className={`fw-bold ${isSuccess ? 'text-success' : 'text-danger'}`}>
          {isSuccess ? '✅ Sucesso' : '❌ Erro'}
        </span>
        <span className="text-muted small">{log.timestamp}</span>
      </div>

      {/* Mensagem */}
      <p className={`mb-2 ${isSuccess ? 'text-success' : 'text-danger'}`}>
        {log.message}
      </p>

      {/* Status code */}
      {log.statusCode && <p className="small text-muted">Status Code: {log.statusCode}</p>}

      {/* Resposta e erro */}
      {log.responseData && <LogDetails title="Resposta" data={log.responseData} />}
      {log.error && <LogDetails title="Erro" data={log.error} isError />}
    </div>
  );
};

// Componente para mostrar detalhes expansíveis
const LogDetails = ({ title, data, isError }) => {
  return (
    <details className="mb-2">
      <summary className={`small ${isError ? 'text-danger' : 'text-muted'} cursor-pointer`}>
        {title}
      </summary>
      <pre className="bg-light p-2 rounded mt-1" style={{ overflowX: 'auto', fontSize: '0.8rem' }}>
        {typeof data === 'object' ? JSON.stringify(data, null, 2) : data}
      </pre>
    </details>
  );
};

export default CronLogs;
