import React, { useState, useEffect } from 'react';
import { Plus, Play, Square, Trash2, Edit, Clock, Globe, Eye, Activity } from 'lucide-react';

const API_BASE_URL = 'http://localhost:3001/api';

// Componente principal
const CronManager = () => {
  const [crons, setCrons] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCron, setEditingCron] = useState(null);
  const [selectedCronLogs, setSelectedCronLogs] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Formulário
  const [formData, setFormData] = useState({
    name: '',
    url: '',
    httpMethod: 'POST',
    body: '',
    schedule: '*/5 * * * *',
    timeZone: 'UTC'
  });

  // Carregar CRONs
  const loadCrons = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons`);
      if (!response.ok) throw new Error('Erro ao carregar CRONs');
      const data = await response.json();
      setCrons(data);
    } catch (err) {
      setError('Erro ao carregar CRONs: ' + err.message);
    }
  };

  // Criar ou atualizar CRON
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const url = editingCron ? 
        `${API_BASE_URL}/crons/${editingCron.id}` : 
        `${API_BASE_URL}/crons`;

      const method = editingCron ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          ...formData,
          body: formData.body || null
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Erro ao salvar CRON');
      }

      await loadCrons();
      resetForm();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Resetar formulário
  const resetForm = () => {
    setFormData({
      name: '',
      url: '',
      httpMethod: 'POST',
      body: '',
      schedule: '*/5 * * * *',
      timeZone: 'UTC'
    });
    setShowForm(false);
    setEditingCron(null);
  };

  // Editar CRON
  const handleEdit = (cron) => {
    setFormData({
      name: cron.name,
      url: cron.url,
      httpMethod: cron.httpMethod,
      body: cron.body || '',
      schedule: cron.schedule,
      timeZone: cron.timeZone
    });
    setEditingCron(cron);
    setShowForm(true);
  };

  // Deletar CRON
  const handleDelete = async (cronId) => {
    if (!window.confirm('Tem certeza que deseja deletar este CRON?')) return;

    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error('Erro ao deletar CRON');
      await loadCrons();
    } catch (err) {
      setError('Erro ao deletar CRON: ' + err.message);
    }
  };

  // Iniciar/Parar CRON
  const toggleCron = async (cronId, isActive) => {
    try {
      const action = isActive ? 'stop' : 'start';
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}/${action}`, {
        method: 'POST'
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `Erro ao ${action} CRON`);
      }

      await loadCrons();
    } catch (err) {
      setError(err.message);
    }
  };

  // Executar CRON manualmente
  const executeCron = async (cronId) => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}/execute`, {
        method: 'POST'
      });

      if (!response.ok) throw new Error('Erro ao executar CRON');

      // Recarregar para ver os logs atualizados
      setTimeout(loadCrons, 1000);
    } catch (err) {
      setError('Erro ao executar CRON: ' + err.message);
    }
  };

  // Ver logs
  const viewLogs = async (cron) => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cron.id}/logs`);
      if (!response.ok) throw new Error('Erro ao carregar logs');
      const logs = await response.json();
      setSelectedCronLogs({ ...cron, logs });
    } catch (err) {
      setError('Erro ao carregar logs: ' + err.message);
    }
  };

  // Carregar CRONs na inicialização
  useEffect(() => {
    loadCrons();
    const interval = setInterval(loadCrons, 30000); // Atualizar a cada 30s
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-7xl mx-auto">
        <div className="bg-white rounded-lg shadow-sm border">
          {/* Header */}
          <div className="border-b border-gray-200 px-6 py-4">
            <div className="flex justify-between items-center">
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Gerenciador de CRONs</h1>
                <p className="text-gray-600 mt-1">
                  {crons.length} CRONs configurados • {crons.filter(c => c.isActive).length} ativos
                </p>
              </div>
              <button
                onClick={() => setShowForm(true)}
                className="bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:bg-blue-700 transition-colors"
              >
                <Plus size={20} />
                Novo CRON
              </button>
            </div>
          </div>

          {/* Mensagem de erro */}
          {error && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 m-6 rounded-lg">
              {error}
              <button 
                onClick={() => setError('')}
                className="float-right text-red-500 hover:text-red-700"
              >
                ×
              </button>
            </div>
          )}

          {/* Lista de CRONs */}
          <div className="p-6">
            {crons.length === 0 ? (
              <div className="text-center py-12">
                <Clock size={48} className="mx-auto text-gray-400 mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">Nenhum CRON configurado</h3>
                <p className="text-gray-600">Crie seu primeiro CRON para começar</p>
              </div>
            ) : (
              <div className="grid gap-4">
                {crons.map(cron => (
                  <div key={cron.id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-2">
                          <h3 className="text-lg font-semibold text-gray-900">{cron.name}</h3>
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                            cron.isActive 
                              ? 'bg-green-100 text-green-800' 
                              : 'bg-gray-100 text-gray-800'
                          }`}>
                            {cron.isActive ? 'Ativo' : 'Inativo'}
                          </span>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 text-sm">
                          <div>
                            <span className="text-gray-500">URL:</span>
                            <p className="font-medium truncate">{cron.url}</p>
                          </div>
                          <div>
                            <span className="text-gray-500">Método:</span>
                            <p className="font-medium">{cron.httpMethod}</p>
                          </div>
                          <div>
                            <span className="text-gray-500">Schedule:</span>
                            <p className="font-medium font-mono">{cron.schedule}</p>
                          </div>
                          <div>
                            <span className="text-gray-500">Timezone:</span>
                            <p className="font-medium flex items-center gap-1">
                              <Globe size={14} />
                              {cron.timeZone}
                            </p>
                          </div>
                        </div>

                        {cron.logs && cron.logs.length > 0 && (
                          <div className="mt-3 text-sm">
                            <span className="text-gray-500">Última execução:</span>
                            <span className={`ml-2 font-medium ${
                              cron.logs[0].status === 'success' ? 'text-green-600' : 'text-red-600'
                            }`}>
                              {cron.logs[0].timestamp} - {cron.logs[0].status === 'success' ? 'Sucesso' : 'Erro'}
                            </span>
                          </div>
                        )}
                      </div>

                      <div className="flex items-center gap-2 ml-4">
                        <button
                          onClick={() => viewLogs(cron)}
                          className="p-2 text-gray-500 hover:text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                          title="Ver logs"
                        >
                          <Eye size={16} />
                        </button>
                        <button
                          onClick={() => executeCron(cron.id)}
                          className="p-2 text-gray-500 hover:text-purple-600 hover:bg-purple-50 rounded-lg transition-colors"
                          title="Executar agora"
                        >
                          <Activity size={16} />
                        </button>
                        <button
                          onClick={() => toggleCron(cron.id, cron.isActive)}
                          className={`p-2 rounded-lg transition-colors ${
                            cron.isActive
                              ? 'text-red-500 hover:text-red-600 hover:bg-red-50'
                              : 'text-green-500 hover:text-green-600 hover:bg-green-50'
                          }`}
                          title={cron.isActive ? 'Parar' : 'Iniciar'}
                        >
                          {cron.isActive ? <Square size={16} /> : <Play size={16} />}
                        </button>
                        <button
                          onClick={() => handleEdit(cron)}
                          className="p-2 text-gray-500 hover:text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                          title="Editar"
                        >
                          <Edit size={16} />
                        </button>
                        <button
                          onClick={() => handleDelete(cron.id)}
                          className="p-2 text-gray-500 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                          title="Deletar"
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Modal do formulário */}
        {showForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg max-w-md w-full max-h-[90vh] overflow-y-auto">
              <div className="p-6">
                <h2 className="text-xl font-bold text-gray-900 mb-4">
                  {editingCron ? 'Editar CRON' : 'Novo CRON'}
                </h2>

                <form onSubmit={handleSubmit} className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Nome *
                    </label>
                    <input
                      type="text"
                      value={formData.name}
                      onChange={(e) => setFormData({...formData, name: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      placeholder="Nome do CRON"
                      required
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      URL *
                    </label>
                    <input
                      type="url"
                      value={formData.url}
                      onChange={(e) => setFormData({...formData, url: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      placeholder="https://exemplo.com/api/webhook"
                      required
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Método HTTP *
                    </label>
                    <select
                      value={formData.httpMethod}
                      onChange={(e) => setFormData({...formData, httpMethod: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                      <option value="GET">GET</option>
                      <option value="POST">POST</option>
                      <option value="PUT">PUT</option>
                      <option value="PATCH">PATCH</option>
                      <option value="DELETE">DELETE</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Body (JSON)
                    </label>
                    <textarea
                      value={formData.body}
                      onChange={(e) => setFormData({...formData, body: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      placeholder='{"message": "Hello from CRON"}'
                      rows={3}
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Schedule (CRON) *
                    </label>
                    <input
                      type="text"
                      value={formData.schedule}
                      onChange={(e) => setFormData({...formData, schedule: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 font-mono"
                      placeholder="*/5 * * * *"
                      required
                    />
                    <p className="text-xs text-gray-500 mt-1">
                      Formato: minuto hora dia mês dia-da-semana
                    </p>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Timezone
                    </label>
                    <select
                      value={formData.timeZone}
                      onChange={(e) => setFormData({...formData, timeZone: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                      <option value="UTC">UTC</option>
                      <option value="America/Sao_Paulo">America/Sao_Paulo</option>
                      <option value="America/New_York">America/New_York</option>
                      <option value="Europe/London">Europe/London</option>
                      <option value="Europe/Lisbon">Europe/Lisbon</option>
                      <option value="Asia/Tokyo">Asia/Tokyo</option>
                    </select>
                  </div>

                  <div className="flex gap-3 pt-4">
                    <button
                      type="submit"
                      disabled={loading}
                      className="flex-1 bg-blue-600 text-white py-3 px-4 rounded-lg hover:bg-blue-700 disabled:bg-blue-400 transition-colors"
                    >
                      {loading ? 'Salvando...' : editingCron ? 'Atualizar' : 'Criar'}
                    </button>
                    <button
                      type="button"
                      onClick={resetForm}
                      className="flex-1 bg-gray-500 text-white py-3 px-4 rounded-lg hover:bg-gray-600 transition-colors"
                    >
                      Cancelar
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}

        {/* Modal de logs */}
        {selectedCronLogs && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg max-w-4xl w-full max-h-[80vh] overflow-hidden">
              <div className="p-6 border-b border-gray-200">
                <div className="flex justify-between items-center">
                  <h2 className="text-xl font-bold text-gray-900">
                    Logs - {selectedCronLogs.name}
                  </h2>
                  <button
                    onClick={() => setSelectedCronLogs(null)}
                    className="text-gray-500 hover:text-gray-700 text-2xl"
                  >
                    ×
                  </button>
                </div>
              </div>

              <div className="p-6 overflow-y-auto max-h-[60vh]">
                {selectedCronLogs.logs.length === 0 ? (
                  <div className="text-center py-8">
                    <Activity size={48} className="mx-auto text-gray-400 mb-4" />
                    <p className="text-gray-600">Nenhum log encontrado</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {selectedCronLogs.logs.map(log => (
                      <div
                        key={log.id}
                        className={`p-4 rounded-lg border-l-4 ${
                          log.status === 'success'
                            ? 'bg-green-50 border-green-400'
                            : 'bg-red-50 border-red-400'
                        }`}
                      >
                        <div className="flex justify-between items-start mb-2">
                          <span className={`font-medium ${
                            log.status === 'success' ? 'text-green-800' : 'text-red-800'
                          }`}>
                            {log.status === 'success' ? '✅ Sucesso' : '❌ Erro'}
                          </span>
                          <span className="text-sm text-gray-500">
                            {log.timestamp}
                          </span>
                        </div>

                        <p className={`text-sm mb-2 ${
                          log.status === 'success' ? 'text-green-700' : 'text-red-700'
                        }`}>
                          {log.message}
                        </p>

                        {log.statusCode && (
                          <p className="text-xs text-gray-600">
                            Status Code: {log.statusCode}
                          </p>
                        )}

                        {log.responseData && (
                          <details className="mt-2">
                            <summary className="text-xs text-gray-600 cursor-pointer">
                              Resposta
                            </summary>
                            <pre className="text-xs bg-gray-100 p-2 rounded mt-1 overflow-x-auto">
                              {typeof log.responseData === 'object' 
                                ? JSON.stringify(log.responseData, null, 2)
                                : log.responseData
                              }
                            </pre>
                          </details>
                        )}

                        {log.error && (
                          <details className="mt-2">
                            <summary className="text-xs text-red-600 cursor-pointer">
                              Erro
                            </summary>
                            <pre className="text-xs bg-red-100 p-2 rounded mt-1 overflow-x-auto">
                              {typeof log.error === 'object' 
                                ? JSON.stringify(log.error, null, 2)
                                : log.error
                              }
                            </pre>
                          </details>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CronManager;
