import React, { useState, useEffect } from 'react';
import { createCron, updateCron } from '../api/cronApi';

const CronForm = ({ editingCron, onClose, onSuccess, onError }) => {
  const [loading, setLoading] = useState(false);
  
  // Dados do formulário
  const [formData, setFormData] = useState({
    name: '',
    url: '',
    httpMethod: 'POST',
    body: '',
    schedule: '*/5 * * * *',
    timeZone: 'UTC'
  });

  // Quando está editando, preencher o formulário
  useEffect(() => {
    if (editingCron) {
      setFormData({
        name: editingCron.name,
        url: editingCron.url,
        httpMethod: editingCron.httpMethod,
        body: editingCron.body || '',
        schedule: editingCron.schedule,
        timeZone: editingCron.timeZone
      });
    }
  }, [editingCron]);

  // Atualizar um campo do formulário
  const handleInputChange = (field, value) => {
    setFormData({
      ...formData,
      [field]: value
    });
  };

  // Enviar formulário
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    onError(''); // Limpar erro anterior

    try {
      const dataToSend = {
        ...formData,
        body: formData.body || null
      };

      if (editingCron) {
        // Editar CRON existente
        await updateCron(editingCron.id, dataToSend);
      } else {
        // Criar novo CRON
        await createCron(dataToSend);
      }

      onSuccess();
    } catch (err) {
      onError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-md w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">
            {editingCron ? 'Editar CRON' : 'Novo CRON'}
          </h2>
          
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Campo Nome */}
            <FormField
              label="Nome *"
              value={formData.name}
              onChange={(value) => handleInputChange('name', value)}
              placeholder="Nome do CRON"
              required
            />

            {/* Campo URL */}
            <FormField
              label="URL *"
              type="url"
              value={formData.url}
              onChange={(value) => handleInputChange('url', value)}
              placeholder="https://exemplo.com/api/webhook"
              required
            />

            {/* Campo Método HTTP */}
            <FormSelect
              label="Método HTTP *"
              value={formData.httpMethod}
              onChange={(value) => handleInputChange('httpMethod', value)}
              options={[
                { value: 'GET', label: 'GET' },
                { value: 'POST', label: 'POST' },
                { value: 'PUT', label: 'PUT' },
                { value: 'PATCH', label: 'PATCH' },
                { value: 'DELETE', label: 'DELETE' }
              ]}
            />

            {/* Campo Body */}
            <FormTextArea
              label="Body (JSON)"
              value={formData.body}
              onChange={(value) => handleInputChange('body', value)}
              placeholder='{"message": "Hello from CRON"}'
              rows={3}
            />

            {/* Campo Schedule */}
            <div>
              <FormField
                label="Schedule (CRON) *"
                value={formData.schedule}
                onChange={(value) => handleInputChange('schedule', value)}
                placeholder="*/5 * * * *"
                className="font-mono"
                required
              />
              <p className="text-xs text-gray-500 mt-1">
                Formato: minuto hora dia mês dia-da-semana
              </p>
            </div>

            {/* Campo Timezone */}
            <FormSelect
              label="Timezone"
              value={formData.timeZone}
              onChange={(value) => handleInputChange('timeZone', value)}
              options={[
                { value: 'UTC', label: 'UTC' },
                { value: 'America/Sao_Paulo', label: 'America/Sao_Paulo' },
                { value: 'America/New_York', label: 'America/New_York' },
                { value: 'Europe/London', label: 'Europe/London' },
                { value: 'Europe/Lisbon', label: 'Europe/Lisbon' },
                { value: 'Asia/Tokyo', label: 'Asia/Tokyo' }
              ]}
            />

            {/* Botões */}
            <div className="flex gap-3 pt-4">
              <button
                type="submit"
                disabled={loading}
                className="flex-1 bg-blue-600 text-white py-3 px-4 rounded-lg hover:bg-blue-700 disabled:bg-blue-400 transition-colors"
              >
                {loading ? 'Salvando...' : editingCron ? 'Atualizar' : 'Criar'}
              </button>
              <button
                type="button"
                onClick={onClose}
                className="flex-1 bg-gray-500 text-white py-3 px-4 rounded-lg hover:bg-gray-600 transition-colors"
              >
                Cancelar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

// Componente para campo de input
const FormField = ({ label, type = 'text', value, onChange, placeholder, required, className = '' }) => {
  return (
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">
        {label}
      </label>
      <input
        type={type}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        className={`w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${className}`}
        placeholder={placeholder}
        required={required}
      />
    </div>
  );
};

// Componente para select
const FormSelect = ({ label, value, onChange, options }) => {
  return (
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">
        {label}
      </label>
      <select
        value={value}
        onChange={(e) => onChange(e.target.value)}
        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
      >
        {options.map(option => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    </div>
  );
};

// Componente para textarea
const FormTextArea = ({ label, value, onChange, placeholder, rows }) => {
  return (
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">
        {label}
      </label>
      <textarea
        value={value}
        onChange={(e) => onChange(e.target.value)}
        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
        placeholder={placeholder}
        rows={rows}
      />
    </div>
  );
};

export default CronForm;
