import { useState, useEffect } from 'react';
import { Plus, Play, Square, Trash2, Edit, Clock, Globe, Eye, Activity } from 'lucide-react';
import 'bootstrap/dist/css/bootstrap.min.css';


const API_BASE_URL = 'http://localhost:3001/api';

const CronManager = () => {
  const [crons, setCrons] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCron, setEditingCron] = useState(null);
  const [selectedCronLogs, setSelectedCronLogs] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [formData, setFormData] = useState({
    name: '',
    url: '',
    httpMethod: 'POST',
    body: '',
    schedule: '*/5 * * * *',
    timeZone: 'UTC'
  });

  const loadCrons = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons`);
      if (!response.ok) throw new Error('Erro ao carregar CRONs');
      const data = await response.json();
      setCrons(data);
    } catch (err) {
      setError('Erro ao carregar CRONs: ' + err.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const url = editingCron
        ? `${API_BASE_URL}/crons/${editingCron.id}`
        : `${API_BASE_URL}/crons`;

      const method = editingCron ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...formData,
          body: formData.body || null
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Erro ao salvar CRON');
      }

      await loadCrons();
      resetForm();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      url: '',
      httpMethod: 'POST',
      body: '',
      schedule: '*/5 * * * *',
      timeZone: 'UTC'
    });
    setShowForm(false);
    setEditingCron(null);
  };

  const handleEdit = (cron) => {
    setFormData({
      name: cron.name,
      url: cron.url,
      httpMethod: cron.httpMethod,
      body: cron.body || '',
      schedule: cron.schedule,
      timeZone: cron.timeZone
    });
    setEditingCron(cron);
    setShowForm(true);
  };

  const handleDelete = async (cronId) => {
    if (!window.confirm('Tem certeza que deseja deletar este CRON?')) return;

    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error('Erro ao deletar CRON');
      await loadCrons();
    } catch (err) {
      setError('Erro ao deletar CRON: ' + err.message);
    }
  };

  const toggleCron = async (cronId, isActive) => {
    try {
      const action = isActive ? 'stop' : 'start';
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}/${action}`, {
        method: 'POST'
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `Erro ao ${action} CRON`);
      }

      await loadCrons();
    } catch (err) {
      setError(err.message);
    }
  };

  const executeCron = async (cronId) => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}/execute`, {
        method: 'POST'
      });

      if (!response.ok) throw new Error('Erro ao executar CRON');

      setTimeout(loadCrons, 1000);
    } catch (err) {
      setError('Erro ao executar CRON: ' + err.message);
    }
  };

  const viewLogs = async (cron) => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cron.id}/logs`);
      if (!response.ok) throw new Error('Erro ao carregar logs');
      const logs = await response.json();
      setSelectedCronLogs({ ...cron, logs });
    } catch (err) {
      setError('Erro ao carregar logs: ' + err.message);
    }
  };

  useEffect(() => {
    loadCrons();
    const interval = setInterval(loadCrons, 30000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-vh-100 bg-light p-4">
      <div className="container">
        <div className="bg-white rounded shadow-sm border">
          {/* Header */}
          <div className="border-bottom px-4 py-3">
            <div className="d-flex justify-content-between align-items-center">
              <div>
                <h1 className="h4 fw-bold text-dark mb-0">Gerenciador de CRONs</h1>
                <p className="text-muted mt-1 mb-0">
                  {crons.length} CRONs configurados • {crons.filter(c => c.isActive).length} ativos
                </p>
              </div>
              <button
                onClick={() => setShowForm(true)}
                className="btn btn-primary d-flex align-items-center gap-2"
              >
                <Plus size={20} />
                Novo CRON
              </button>
            </div>
          </div>

          {/* Mensagem de erro */}
          {error && (
            <div className="alert alert-danger m-4">
              {error}
              <button
                onClick={() => setError('')}
                type="button"
                className="btn-close float-end"
              ></button>
            </div>
          )}

          {/* Lista de CRONs */}
          <div className="p-4">
            {crons.length === 0 ? (
              <div className="text-center py-5">
                <Clock size={48} className="mx-auto text-muted mb-3" />
                <h3 className="h5 fw-medium text-dark mb-2">Nenhum CRON configurado</h3>
                <p className="text-muted">Crie seu primeiro CRON para começar</p>
              </div>
            ) : (
              <div className="d-grid gap-3">
                {crons.map(cron => (
                  <div
                    key={cron.id}
                    className="border rounded p-3 hover-shadow-sm"
                  >
                    <div className="d-flex justify-content-between">
                      <div className="flex-grow-1">
                        <div className="d-flex align-items-center gap-2 mb-2">
                          <h3 className="h6 fw-semibold text-dark mb-0">{cron.name}</h3>
                          <span
                            className={`badge ${cron.isActive ? 'bg-success' : 'bg-secondary'}`}
                          >
                            {cron.isActive ? 'Ativo' : 'Inativo'}
                          </span>
                        </div>

                        <div className="row g-3 small">
                          <div className="col-md-3">
                            <span className="text-muted">URL:</span>
                            <p className="fw-medium text-truncate mb-0">{cron.url}</p>
                          </div>
                          <div className="col-md-3">
                            <span className="text-muted">Método:</span>
                            <p className="fw-medium mb-0">{cron.httpMethod}</p>
                          </div>
                          <div className="col-md-3">
                            <span className="text-muted">Schedule:</span>
                            <p className="fw-medium font-monospace mb-0">{cron.schedule}</p>
                          </div>
                          <div className="col-md-3">
                            <span className="text-muted">Timezone:</span>
                            <p className="fw-medium d-flex align-items-center gap-1 mb-0">
                              <Globe size={14} />
                              {cron.timeZone}
                            </p>
                          </div>
                        </div>

                        {cron.logs && cron.logs.length > 0 && (
                          <div className="mt-3 small">
                            <span className="text-muted">Última execução:</span>
                            <span
                              className={`ms-2 fw-medium ${
                                cron.logs[0].status === 'success'
                                  ? 'text-success'
                                  : 'text-danger'
                              }`}
                            >
                              {cron.logs[0].timestamp} -{' '}
                              {cron.logs[0].status === 'success' ? 'Sucesso' : 'Erro'}
                            </span>
                          </div>
                        )}
                      </div>

                      <div className="d-flex align-items-start gap-2 ms-3">
                        <button
                          onClick={() => viewLogs(cron)}
                          className="btn btn-outline-secondary btn-sm"
                          title="Ver logs"
                        >
                          <Eye size={16} />
                        </button>
                        <button
                          onClick={() => executeCron(cron.id)}
                          className="btn btn-outline-primary btn-sm"
                          title="Executar agora"
                        >
                          <Activity size={16} />
                        </button>
                        <button
                          onClick={() => toggleCron(cron.id, cron.isActive)}
                          className={`btn btn-sm ${
                            cron.isActive
                              ? 'btn-outline-danger'
                              : 'btn-outline-success'
                          }`}
                          title={cron.isActive ? 'Parar' : 'Iniciar'}
                        >
                          {cron.isActive ? <Square size={16} /> : <Play size={16} />}
                        </button>
                        <button
                          onClick={() => handleEdit(cron)}
                          className="btn btn-outline-info btn-sm"
                          title="Editar"
                        >
                          <Edit size={16} />
                        </button>
                        <button
                          onClick={() => handleDelete(cron.id)}
                          className="btn btn-outline-danger btn-sm"
                          title="Deletar"
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CronManager;
