import React, { useState, useEffect } from 'react';
import { Plus, Play, Square, Trash2, Edit, Clock, Globe, Eye, Activity } from 'lucide-react';

const API_BASE_URL = 'http://localhost:3001/api';

const CronManager = () => {
  const [crons, setCrons] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCron, setEditingCron] = useState(null);
  const [selectedCronLogs, setSelectedCronLogs] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [formData, setFormData] = useState({
    name: '',
    url: '',
    httpMethod: 'POST',
    body: '',
    schedule: '*/5 * * * *',
    timeZone: 'UTC'
  });

  const loadCrons = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons`);
      if (!response.ok) throw new Error('Erro ao carregar CRONs');
      const data = await response.json();
      setCrons(data);
    } catch (err) {
      setError('Erro ao carregar CRONs: ' + err.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const url = editingCron ?
        `${API_BASE_URL}/crons/${editingCron.id}` :
        `${API_BASE_URL}/crons`;

      const method = editingCron ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...formData,
          body: formData.body || null
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Erro ao salvar CRON');
      }

      await loadCrons();
      resetForm();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      url: '',
      httpMethod: 'POST',
      body: '',
      schedule: '*/5 * * * *',
      timeZone: 'UTC'
    });
    setShowForm(false);
    setEditingCron(null);
  };

  const handleEdit = (cron) => {
    setFormData({
      name: cron.name,
      url: cron.url,
      httpMethod: cron.httpMethod,
      body: cron.body || '',
      schedule: cron.schedule,
      timeZone: cron.timeZone
    });
    setEditingCron(cron);
    setShowForm(true);
  };

  const handleDelete = async (cronId) => {
    if (!window.confirm('Tem certeza que deseja deletar este CRON?')) return;

    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error('Erro ao deletar CRON');
      await loadCrons();
    } catch (err) {
      setError('Erro ao deletar CRON: ' + err.message);
    }
  };

  const toggleCron = async (cronId, isActive) => {
    try {
      const action = isActive ? 'stop' : 'start';
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}/${action}`, {
        method: 'POST'
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `Erro ao ${action} CRON`);
      }

      await loadCrons();
    } catch (err) {
      setError(err.message);
    }
  };

  const executeCron = async (cronId) => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cronId}/execute`, {
        method: 'POST'
      });

      if (!response.ok) throw new Error('Erro ao executar CRON');

      setTimeout(loadCrons, 1000);
    } catch (err) {
      setError('Erro ao executar CRON: ' + err.message);
    }
  };

  const viewLogs = async (cron) => {
    try {
      const response = await fetch(`${API_BASE_URL}/crons/${cron.id}/logs`);
      if (!response.ok) throw new Error('Erro ao carregar logs');
      const logs = await response.json();
      setSelectedCronLogs({ ...cron, logs });
    } catch (err) {
      setError('Erro ao carregar logs: ' + err.message);
    }
  };

  useEffect(() => {
    loadCrons();
    const interval = setInterval(loadCrons, 30000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="container py-4">
      <div className="card shadow-sm">
        <div className="card-header d-flex justify-content-between align-items-center">
          <div>
            <h1 className="h4 mb-0">Gerenciador de CRONs</h1>
            <small className="text-muted">
              {crons.length} CRONs configurados • {crons.filter(c => c.isActive).length} ativos
            </small>
          </div>
          <button
            onClick={() => setShowForm(true)}
            className="btn btn-primary d-flex align-items-center gap-2"
          >
            <Plus size={20} /> Novo CRON
          </button>
        </div>

        {error && (
          <div className="alert alert-danger m-3 d-flex justify-content-between">
            {error}
            <button type="button" className="btn-close" onClick={() => setError('')}></button>
          </div>
        )}

        <div className="card-body">
          {crons.length === 0 ? (
            <div className="text-center py-5">
              <Clock size={48} className="text-muted mb-3" />
              <h5>Nenhum CRON configurado</h5>
              <p className="text-muted">Crie seu primeiro CRON para começar</p>
            </div>
          ) : (
            <div className="row g-3">
              {crons.map(cron => (
                <div key={cron.id} className="col-12">
                  <div className="border rounded p-3">
                    <div className="d-flex justify-content-between">
                      <div>
                        <h5 className="mb-1">{cron.name}</h5>
                        <span className={`badge ${cron.isActive ? 'bg-success' : 'bg-secondary'}`}>
                          {cron.isActive ? 'Ativo' : 'Inativo'}
                        </span>
                        <div className="mt-2 small">
                          <div><strong>URL:</strong> {cron.url}</div>
                          <div><strong>Método:</strong> {cron.httpMethod}</div>
                          <div><strong>Schedule:</strong> {cron.schedule}</div>
                          <div className="d-flex align-items-center">
                            <Globe size={14} className="me-1" /> {cron.timeZone}
                          </div>
                        </div>
                      </div>
                      <div className="d-flex flex-column gap-2">
                        <button className="btn btn-outline-secondary btn-sm" onClick={() => viewLogs(cron)}><Eye size={16} /></button>
                        <button className="btn btn-outline-primary btn-sm" onClick={() => executeCron(cron.id)}><Activity size={16} /></button>
                        <button className={`btn btn-sm ${cron.isActive ? 'btn-outline-danger' : 'btn-outline-success'}`} onClick={() => toggleCron(cron.id, cron.isActive)}>
                          {cron.isActive ? <Square size={16} /> : <Play size={16} />}
                        </button>
                        <button className="btn btn-outline-info btn-sm" onClick={() => handleEdit(cron)}><Edit size={16} /></button>
                        <button className="btn btn-outline-danger btn-sm" onClick={() => handleDelete(cron.id)}><Trash2 size={16} /></button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Modal Form */}
      {showForm && (
        <div className="modal fade show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">{editingCron ? 'Editar CRON' : 'Novo CRON'}</h5>
                <button type="button" className="btn-close" onClick={resetForm}></button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label">Nome *</label>
                    <input type="text" className="form-control" value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} required />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">URL *</label>
                    <input type="url" className="form-control" value={formData.url} onChange={(e) => setFormData({ ...formData, url: e.target.value })} required />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Método HTTP *</label>
                    <select className="form-select" value={formData.httpMethod} onChange={(e) => setFormData({ ...formData, httpMethod: e.target.value })}>
                      <option>GET</option>
                      <option>POST</option>
                      <option>PUT</option>
                      <option>PATCH</option>
                      <option>DELETE</option>
                    </select>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Body (JSON)</label>
                    <textarea className="form-control" rows="3" value={formData.body} onChange={(e) => setFormData({ ...formData, body: e.target.value })}></textarea>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Schedule (CRON) *</label>
                    <input type="text" className="form-control" value={formData.schedule} onChange={(e) => setFormData({ ...formData, schedule: e.target.value })} required />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Timezone</label>
                    <select className="form-select" value={formData.timeZone} onChange={(e) => setFormData({ ...formData, timeZone: e.target.value })}>
                      <option value="UTC">UTC</option>
                      <option value="America/Sao_Paulo">America/Sao_Paulo</option>
                      <option value="America/New_York">America/New_York</option>
                      <option value="Europe/London">Europe/London</option>
                      <option value="Europe/Lisbon">Europe/Lisbon</option>
                      <option value="Asia/Tokyo">Asia/Tokyo</option>
                    </select>
                  </div>
                </div>
                <div className="modal-footer">
                  <button type="submit" className="btn btn-primary" disabled={loading}>
                    {loading ? 'Salvando...' : editingCron ? 'Atualizar' : 'Criar'}
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={resetForm}>Cancelar</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Modal Logs */}
      {selectedCronLogs && (
        <div className="modal fade show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Logs - {selectedCronLogs.name}</h5>
                <button type="button" className="btn-close" onClick={() => setSelectedCronLogs(null)}></button>
              </div>
              <div className="modal-body" style={{ maxHeight: '60vh', overflowY: 'auto' }}>
                {selectedCronLogs.logs.length === 0 ? (
                  <div className="text-center py-4 text-muted">
                    <Activity size={48} className="mb-3" />
                    Nenhum log encontrado
                  </div>
                ) : (
                  selectedCronLogs.logs.map(log => (
                    <div key={log.id} className={`border-start p-3 mb-3 ${log.status === 'success' ? 'border-success bg-light' : 'border-danger bg-light'}`}>
                      <div className="d-flex justify-content-between">
                        <strong className={log.status === 'success' ? 'text-success' : 'text-danger'}>
                          {log.status === 'success' ? '✅ Sucesso' : '❌ Erro'}
                        </strong>
                        <small className="text-muted">{log.timestamp}</small>
                      </div>
                      <p className={log.status === 'success' ? 'text-success mb-1' : 'text-danger mb-1'}>{log.message}</p>
                      {log.statusCode && <small className="text-muted">Status Code: {log.statusCode}</small>}
                      {log.responseData && (
                        <details className="mt-2">
                          <summary>Resposta</summary>
                          <pre className="bg-light p-2 border rounded">{JSON.stringify(log.responseData, null, 2)}</pre>
                        </details>
                      )}
                      {log.error && (
                        <details className="mt-2">
                          <summary className="text-danger">Erro</summary>
                          <pre className="bg-light p-2 border rounded">{JSON.stringify(log.error, null, 2)}</pre>
                        </details>
                      )}
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        </div>
      )}

    </div>
  );
};

export default CronManager;
import 'bootstrap/dist/css/bootstrap.min.css';
